let getValueByKey = (data: string, key: string) { 
    let helper = (data: string, key: string) {
        let indexStart = indexof(data, key);
        let endString = '"';
        let sub = substring(data, indexStart + strlen(key) + 11); //removing "value":" 11 is the length
        let indexEnd = indexof(sub, endString);
        let value = substring(sub, 0, indexEnd);
        value;
    };
    let endString = '"';
    let condtion  = data contains key;
    iff(condtion, helper(data, key), "N/A");
};
SigninLogs
| where RiskState == "atRisk" 
| where IPAddress !in (split('{FilterByIPAddress}',","))
| where RiskLevelDuringSignIn in (split('{FilterBySignInRiskLevel}',","))
| where Location in (split('{FilterByCountry}',",")) 
| summarize total1 = count() by CorrelationId
| join kind=leftouter ( AuditLogs 
    | extend b2cpolicy = getValueByKey(tostring(AdditionalDetails), "PolicyId")
    | where b2cpolicy != "N/A"
    | distinct CorrelationId, b2cpolicy) on CorrelationId  
| project total1, b2cpolicy, CorrelationId
| summarize  policyCount= count(), TotalSum = sum(total1) by b2cpolicy
| order by TotalSum

